import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/shorturls';

// Create a short URL
export const createShortUrl = async (url, validity, shortcode) => {
  try {
    const response = await axios.post(API_BASE_URL, { url, validity, shortcode });
    return response.data;
  } catch (error) {
    return Promise.reject(
      error.response?.data || { message: 'An error occurred while creating the short URL.' }
    );
  }
};

// Get statistics for a specific short URL
export const getShortUrlStatistics = async (shortcode) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/${shortcode}`);
    return response.data;
  } catch (error) {
    return Promise.reject(
      error.response?.data || { message: 'An error occurred while retrieving statistics.' }
    );
  }
};

// Fetch all short URLs statistics
export const fetchAllStatistics = async () => {
  try {
    const response = await axios.get(API_BASE_URL);
    return response.data; // Expecting array of all short URLs
  } catch (error) {
    return Promise.reject(
      error.response?.data || { message: 'An error occurred while fetching all statistics.' }
    );
  }
};

// Optional default export
export default {
  createShortUrl,
  getShortUrlStatistics,
  fetchAllStatistics,
};
