import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/shorturls';

export const createShortUrl = async (url, validity, shortcode) => {
    try {
        const response = await axios.post(API_BASE_URL, { url, validity, shortcode });
        return response.data;
    } catch (error) {
        throw error.response ? error.response.data : { message: 'An error occurred while creating the short URL.' };
    }
};

export const getShortUrlStatistics = async (shortcode) => {
    try {
        const response = await axios.get(`${API_BASE_URL}/${shortcode}`);
        return response.data;
    } catch (error) {
        throw error.response ? error.response.data : { message: 'An error occurred while retrieving statistics.' };
    }
};
